list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX vcard: <http://www.w3.org/2006/vcard/ns#> PREFIX fn: <http://www.w3.org/2005/xpath-functions#> PREFIX tmp: <http://localhost/display#>
SELECT DISTINCT ?authorship ?name ?addressNumber ?person WHERE { ?subject ?property ?authorship . ?authorship rdfs:label ?name . OPTIONAL { ?authorship core:rank ?rank } OPTIONAL { ?authorship tmp:addr ?addressNumber }
FILTER ( bound(?authorship) )
} ORDER BY ?rank (fn:lower-case(?name))

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX foaf:  <http://xmlns.com/foaf/0.1/>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX vcard:  <http://www.w3.org/2006/vcard/ns#>
PREFIX wos: <http://webofscience.com/ontology/wos#>
PREFIX tmp: <http://localhost/display#>

CONSTRUCT {
    ?subject ?property ?authorship .

    ?authorship a core:Authorship .
    ?authorship core:rank ?rank .
    ?authorship rdfs:label ?name .
    ?authorship tmp:addr ?addrs .

} WHERE {
    {
        ?subject ?property ?authorship .
        ?authorship a core:Authorship
    } UNION {
        ?subject ?property ?authorship .
        ?authorship a core:Authorship .
        ?authorship core:rank ?rank .
        ?authorship rdfs:label ?name .
    } UNION {
        ?subject ?property ?authorship .
        ?authorship a core:Authorship .
        {
            select ?authorship (group_concat(distinct ?number ; separator = ", ") as ?addrs)
            where {
                ?subject ?property ?authorship .
                ?authorship a core:Authorship .
                ?authorship core:relates ?address .
                ?address a wos:Address .
                ?address wos:sequenceNumber ?number.
            }
            GROUP BY ?authorship
        }
    }
}
